import React, { useState } from 'react';
import {
  Row,
  Col,
  Form,
  ButtonGroup,
  InputGroup,
  Button
} from 'react-bootstrap';

// functional component to add and remove options
// class component for create poll form
const CreatePoll = (props) => {
  const [question, setQuestion] = useState("")  
  const [options, setOptions] = useState(['',''])

  const addOption = () => {
      const values = options;
      if (values.length < 10){
        values.push('');
      }
      setOptions(values);
    console.log(options)
    }

    const removeOption = () => {
      const values = options;
      if (values.length > 2){
        values.pop();
      }
      setOptions(values);
    }

    const handleChangeInput= (event, i) => {
      console.log(i);
      const values = options;
      values[i] = event.target.value;
      setOptions(values);
    }

  const OptionGroup = (props) => {
    const count = props.count;
    return (
      <Form.Group className="mb-3" controlId={'Option'+(count+1)}>
      <InputGroup>
        <InputGroup.Text
          id = {'Option'+(count+1)}
        >Option {count+1}</InputGroup.Text>
        <Form.Control
          className="col-sm-10"
          type="text" 
          aria-describedby={'Option'+(count+1)}
          value={options[count]}
          onChange = {(event)=>(handleChangeInput(event, count))}
        />
      </InputGroup>
      </Form.Group>
    );
  };


  return (
    <>
    <Form>
      <Form.Group className="mb-3" controlId="">
        <Form.Label>Question</Form.Label>
        <Form.Control 
          value={question}
          onChange={(e)=>setQuestion(e.target.value)}
          as="textarea" rows={2} 
          placeholder="Enter Question you want to ask" />
      </Form.Group>

      {options.map((option, i) => (
          <OptionGroup count={i} 
          />
        )
      )}

    <Form.Group className="mt-3">
    <ButtonGroup aria-label="Basic example">
      <Button variant="success"
        onClick={()=>addOption()}
      >Add</Button>
      <Button variant="secondary"
        onClick={()=>removeOption()}
      >Remove</Button>
    </ButtonGroup>
    </Form.Group>
    <hr/>
    <Form.Group className='mt-3 text-end'>
      <Button className='m-2' onClick={props.onHide}>Close</Button>
      <Button className='m-2' variant="primary" type="submit">
        Submit
      </Button>
    </Form.Group>
    </Form>
    </>
  );

};

export default CreatePoll;
