import React, { useState, useContext, useEffect } from "react";
import axios from 'axios';
import { useParams } from 'react-router-dom';
import {
  Row,
  Col
} from 'react-bootstrap';

import { Layout } from '../layout/Layout';
import { Context } from '../context/appContext';
import { 
  Pollform,
  Pollstatus
} from '../components';

const Pollpage = () => {
  const { poll_id } = useParams();
  const [ loading, setLoading ] = useState(true);
  const [ poll, setPoll ] = useState(null);
  const token = useContext(Context).store.token;

  console.log('poll id received:',poll_id);

  const opts = {
      method: "GET",
      headers: {
        "Accept":"application/json",
        "Authorization": "Bearer "+ token
      },
      params: {"poll_id":poll_id}
  }
  useEffect(()=> {
  axios.get(process.env.REACT_APP_API_SERVER+"/api/polls/getpoll", opts)
    .then((resp)=>{
      if (resp.status == 200){
      setPoll(resp.data);
      setLoading(false);
      }
    });
  }, [])
  console.log(poll);

  return (
    <Layout heading="Poll'O">
    {loading ? (
        <>
        <p>Loading</p>
        </>
      ):(
        <Row>
        { !poll.polldata.attempted &&
          <Col>
          </Col>
        }
        <Col>
        <Pollform poll = {poll} />
        </Col>
        </Row>
      )
    }
    </Layout>
  );
};

export default Pollpage;
