import React, { Component } from 'react';
import {
  Form,
  ButtonGroup,
  Button
} from 'react-bootstrap';

const OptionGroup = (props) => {
  const count = props.count;
  return (
    <Form.Group as="row" controlId={'Option'+(count+1)}>
      <Form.Label>Option {count+1}</Form.Label>
      <Form.Control 
        type="text" 
        value={props.value[count]['option']}
        onChange = {(event)=>(props.handleChangeInput(event, count))}
      />
    </Form.Group>
  );
};

class CreatePoll extends Component{
  constructor(props) {
    super(props);
    this.state = {
      countOptions : 1,
      question: null,
      options: [{option: ''},]
    };
  }

  addOption = () => {
    const values = this.state;
    values.options.push({option: ''});
    this.setState(values)
  }

  removeOption = () => {
    const values = this.state;
    values.options.pop();
    this.setState(values)
  }

  handleChangeInput= (event, i) => {
    const values = this.state;
    values.options[i]['option'] = event.target.value;
    this.setState(values);
  }

  render() {
    return (
      <>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>

        {this.state.options.map((option, i) => (
            <OptionGroup count={i} 
            value = {this.state.options}
            handleChangeInput={this.handleChangeInput}
            />
          )
        )}

      <Form.Group className="mt-3">
      <ButtonGroup aria-label="Basic example">
        <Button variant="success"
          onClick={()=>(this.addOption())}
        >Add</Button>
        <Button variant="secondary"
          onClick={()=>(this.removeOption())}
        >Remove</Button>
      </ButtonGroup>
      </Form.Group>

      <Form.Group className='mt-3 justify-content-end'>
        <Button onClick={this.props.onHide}>Close</Button>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form.Group>
      </Form>
      </>
    );
  }
};

export default CreatePoll;
