import React, { useState } from "react";
import { Button } from "react-bootstrap";

import { Layout } from '../layout/Layout';
import { 
  AllPolls,
  WelcomeMessage
} from '../components';

const Home = () => {
  const [viewPolls, setViewPolls] = useState(false);

  return (
    <Layout>
       { (viewPolls) ? (
           <AllPolls />
       ) : (
           <>
           <WelcomeMessage handleClick={setViewPolls} />
           <Button 
            variant='primary'
            onClick={()=>(setViewPolls(true))}
            >View Polls</Button>
           </> 
       )

       }
    </Layout>
  );
};

export default Home;
