import React, { Component } from 'react';
import {
  Form,
  Modal,
  ButtonGroup,
  Button
} from 'react-bootstrap';

const OptionGroup = (props) => {
  const count = props.count;
  return (
    <Form.Group as="row" className="mb-3" controlId={'Option'+(count+1)}>
      <Form.Label>Option {count+1}</Form.Label>
      <Form.Control 
        type="text" 
        value={props.value[count]['option']}
        onChange = {(event)=>(props.handleChangeInput(event, count))}
      />
    </Form.Group>
  );
};

class ModalCreatePoll extends Component{
  constructor(props) {
    super(props);
    this.state = {
      countOptions : 1,
      question: null,
      options: ['', '']
    };
  }

  addOption = () => {
    const values = this.state;
    if (values.options.length < 10){
      values.options.push('');
    }

    this.setState(values);
  }

  removeOption = () => {
    const values = this.state;
    if (values.options.length > 2){
      values.options.pop()}
    this.setState(values)
  }

  handleChangeInput= (event, i) => {
    const values = this.state;
    values.options[i] = event.target.value;
    this.setState(values);
  }

  render() {
    return (
      <>
      <Modal
      {...this.props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Create Poll
        </Modal.Title>
      </Modal.Header>
      <Form>
      <Modal.Body>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>

        {this.state.options.map((option, i) => (
            <OptionGroup count={i} 
            value = {this.state.options}
            handleChangeInput={this.handleChangeInput}
            />
          )
        )}

      <Form.Group className="mt-3">
      <ButtonGroup aria-label="Basic example">
        <Button variant="success"
          onClick={()=>(this.addOption())}
        >Add</Button>
        <Button variant="secondary"
          onClick={()=>(this.removeOption())}
        >Remove</Button>
      </ButtonGroup>
      </Form.Group>

      </Modal.Body>
      <Modal.Footer>
        <Button onClick={this.props.onHide}>Close</Button>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Modal.Footer>
      </Form>
    </Modal>
      </>
    );
  }
};

export default ModalCreatePoll;
