import React, { useState,useContext } from 'react';
import { Col,Card,Collapse,Button } from 'react-bootstrap';
import { Context } from "../context/appContext";

const Pollcard = (props) => {
  const poll = props.polldata;
  const [open, setOpen] = useState(false);
  return (
    <>
    <Col>
    <Card border="primary" className="mb-3" >
      <Card.Body
        onClick={() => setOpen(!open)}
        aria-controls="example-collapse-text"
        aria-expanded={open}

      >
        <Card.Title>{poll.question}</Card.Title>
        { useContext(Context).store.isAuthenticated &&
        <Button
         variant = 'primary'
        >Check out</Button>
        }

      <Collapse in={open}>
        <Card.Text id="example-collapse-text">
          Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus
          terry richardson ad squid. Nihil anim keffiyeh helvetica, craft beer
          labore wes anderson cred nesciunt sapiente ea proident.
        </Card.Text>  
      </Collapse>
      <Button
        onClick={() => setOpen(!open)}
        aria-controls="example-collapse-text"
        aria-expanded={open}
      >
        Check Out
      </Button>

      </Card.Body>
      <Card.Footer className="text-muted text-end">Poll created on</Card.Footer>
    </Card>
    </Col>
    </>
  );
};

export default Pollcard;
