import React, { useState } from 'react';
import {
  Row,
  Col,
  Form,
  ButtonGroup,
  InputGroup,
  Button
} from 'react-bootstrap';

// functional component to add and remove options
const OptionGroup = (props) => {
    const count = props.count;
    return (
      <Form.Group className="mb-3" controlId={'Option'+(count+1)}>
      <InputGroup>
        <InputGroup.Text
          id = {'Option'+(count+1)}
        >Option {count+1}</InputGroup.Text>
        <Form.Control
          className="col-sm-10"
          type="text" 
          aria-describedby={'Option'+(count+1)}
          value={props.value[count]}
          onChange = {(event)=>(handleChangeInput(event, count))}
        />
      </InputGroup>
      </Form.Group>
    );
  };

// class component for create poll form
class CreatePoll extends React.Component {
  constructor(props){
    super(props);
    this.state={
    question:"",
    options:["",""]
    }
  }

  addOption = () => {
      const values = polldata
      if (values.options.length < 10){
        values.options.push('');
      }
      setPolldata(values);

    }

   removeOption = () => {
      const values = polldata;
      if (values.options.length > 2){
        values.options.pop();
      }
      setPolldata(values);
    }

   handleChangeInput= (event, i) => {
      console.log(i);
      const values = polldata;
      values.options[i] = event.target.value;
      setPolldata(values);
    }


  render() {
    return (
    <>
    <Form>
      <Form.Group className="mb-3" controlId="">
        <Form.Label>Question</Form.Label>
        <Form.Control 
          value={this.state.question}
          onChange={(e)=>handleChangeInput(e)}
          as="textarea" rows={2} 
          placeholder="Enter Question you want to ask" />
      </Form.Group>

      {polldata.options.map((option, i) => (
          <OptionGroup count={i}
            value = {option}
          />
        )
      )}

    <Form.Group className="mt-3">
    <ButtonGroup aria-label="Basic example">
      <Button variant="success"
        onClick={()=>this.addOption()}
      >Add</Button>
      <Button variant="secondary"
        onClick={()=>this.removeOption()}
      >Remove</Button>
    </ButtonGroup>
    </Form.Group>
    <hr/>
    <Form.Group className='mt-3 text-end'>
      <Button className='m-2' onClick={props.onHide}>Close</Button>
      <Button className='m-2' variant="primary" type="submit">
        Submit
      </Button>
    </Form.Group>
    </Form>
    </>
  );
 }

};

export default CreatePoll;
