import React, { useState } from "react";
import {
  Row,
  Modal,
  Tabs,
  Tab
} from "react-bootstrap";

import { LoginForm, RegisterForm } from './index';
import './ModalLogin.css';

const ModalLogin = (props) => {
  const [ loginSuccess, setLoginSuccess ] = useState(false);

  const handleSuccess = ()=>{
    props.setModalShow(null);
  }

  return (
   <Modal
     {...props}
     size="lg"
     aria-labelledby="contained-modal-title-vcenter"
     centered
   >
     <Modal.Header closeButton>
       <Modal.Title>Sign In</Modal.Title>
     </Modal.Header>

     <Modal.Body>
    <Row>
     <Tabs defaultActiveKey="login" 
       id="LoginReg-tab"
       className="container-fluid"
    >
       <Tab eventKey="login" title="Login" tabClassName="inline-tab">
          <LoginForm handleSuccess={handleSuccess}/>
       </Tab>

       <Tab eventKey="register" title="Register" tabClassName="inline-tab">
           <RegisterForm handleSuccess={handleSuccess} />
       </Tab>

     </Tabs> 
     </Row> 
     </Modal.Body>
   </Modal>
 );
};

export default ModalLogin;
